operation.add:
/* Header menu */						
$headermenus = array();

if ($this->user->hasPermission('access', 'extension/headermenu')) {		
	$headermenus[] = array(
		'name'	   => $this->language->get('text_headermenu'),
		'href'     => $this->url->link('extension/headermenu', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()
	);
}

if ($this->user->hasPermission('access', 'extension/footertitle')) {		
	$headermenus[] = array(
		'name'	   => $this->language->get('text_footertitle'),
		'href'     => $this->url->link('extension/footertitle', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()
	);
}

if ($this->user->hasPermission('access', 'extension/footerlink')) {		
	$headermenus[] = array(
		'name'	   => $this->language->get('text_footerlink'),
		'href'     => $this->url->link('extension/footerlink', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()
	);
}

if ($this->user->hasPermission('access', 'extension/module/account_sidebar')) {		
	$headermenus[] = array(
		'name'	   => '會員功能選單',
		'href'     => $this->url->link('extension/module/account_sidebar', 'user_token=' . $this->session->data['user_token'], true),
		'children' => array()
	);
}

if ($headermenus) {					
	$data['menus'][] = array(
		'id'       => 'menu-megafooter',
		'icon'	   => 'fa fa-share-alt fa-fw', 
		'name'	   => $this->language->get('text_menu'),
		'href'     => '',
		'children' => $headermenus
	);		
}


/* Header menu */
